    //https://docs.servicenow.com/bundle/xanadu-integrate-applications/page/administer/import-sets/reference/r_FileTypeDataSource.html
    res.forEach(function(item) {
        var obj = {};
        for (i in item)
            obj[i] = item[i].toString();
        result.addRow(obj);
