sys_script_include.script:
var LCR_TranslateWithGoogle = Class.create();
LCR_TranslateWithGoogle.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    translate: function() {
        var sw = new sn_ws.RESTMessageV2('RapidAPI', 'Google Translate ');
        sw.setStringParameterNoEscape('method', this.getParameter('method'));
        sw.setStringParameterNoEscape('question', this.getParameter('question'));
        sw.setStringParameterNoEscape('target_lang', this.getParameter('target_lang'));
        sw.setStringParameterNoEscape('source_lang', this.getParameter('source_lang'));
        sw.setHttpMethod('post');
        var res = sw.execute();
        var resBody = JSON.parse(res.getBody());
        if (res.getStatusCode() == 200) {
            return resBody.data.translations[0].translatedText;
        } else {
            return 'something went wrong status code= ' + res.getStatusCode() + '. Error message: ' + res.getErrorMessage();
        }
    },

    type: 'LCR_TranslateWithGoogle'
});
