sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
	try{
	var res ={};
    var cart = new sn_sc.CartJS();
    // var item = {
    //     "sysparm_id": "2122cefe97ae0610f99bfcdfe153afcf",
    //     "sysparm_quantity": "1",
    //     "variables": {
    //         "test_variable": request.body.data.test_variable
    //     }
	var item = request.body.data;
	gs.error("chaitanya-->"+JSON.stringify(request.body.data));

    cart.addToCart(item);
    var info = cart.checkoutCart();
	res.reqID = info.request_id;
	var ritmGr = new GlideRecord('sc_req_item');
	if(ritmGr.get('request',info.request_id)){
		res.ritmID = ritmGr.getValue('sys_id');
		var scGr = new GlideRecord('sc_task');
		if(scGr.get('request_item',ritmGr.getValue('sys_id')))
		res.scTaskID = scGr.getValue('sys_id');

	}
	response.setBody(res);
	response.setStatus(200);
	}
	catch(err){
		response.setError(JSON.stringify(err));
		response.setStatus(405);
	}

})(request, response);
