sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
    let pathParm = request.pathParams;
    let method = pathParm.method;
    let qp = request.queryParams;
    if (method == 'sum') {
        response.setStatus(200);
        response.setBody({
            result: parseInt(qp.num1) + parseInt(qp.num2)
        });
    } else if (method == 'dif') {
        response.setStatus(200);
        response.setBody({
            result: qp.num1 - qp.num2
        });
    } else if (method == 'mul') {
        response.setStatus(200);
        response.setBody({
            result: qp.num1 * qp.num2
        });
    } else if (method == 'div') {
        response.setStatus(200);
        response.setBody({
            result: qp.num1 / qp.num2
        });
    } else if (method == 'getDetails') {
        response.setBody({
            uri: request.uri,
            url: request.url,
            getSupportedResponseContentTypes: request.getSupportedResponseContentTypes(),
            getheaders: request.headers,
            queryString: request.queryString,
            queryParams: request.queryParams,
            // a:request.
        });
    } else if (method == 'testCustomError') {
        var custError = new sn_ws_err.ServiceError();
        custError.setStatus(418);
        custError.setDetail('this is a custom error using sn_ws_err.ServiceError()');
        custError.setMessage('custom error');
		return custError

    } else {
        // response.setStatus(200);
        // response.setBody({result:qp.num1 + qp.num2});

        response.setError(new sn_ws_err.NotFoundError('this api supports only sum,dif,mul,div methods please try those'));
    }

})(request, response);
